mapper.select:
SELECT b.board_id AS boardId, c.name AS category, b.writer, b.title, b.views, b.created_date AS generationTimestamp, b.modified_date AS modificationTimestamp, count(f.board_id) AS fileCount FROM board b LEFT JOIN category c ON c.category_id = b.category_id LEFT JOIN file f ON f.board_id = b.board_id WHERE b.created_date >= #{startDate} AND b.created_date <= #{endDate}
AND (b.title LIKE CONCAT('%', #{search}, '%') OR b.writer LIKE CONCAT('%', #{search}, '%') OR b.content LIKE CONCAT('%', #{search}, '%'))
AND c.name = #{category}
GROUP BY b.board_id, c.name, b.writer, b.title, b.views, b.created_date, b.modified_date ORDER BY b.board_id DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(board_id) FROM board b WHERE b.created_date >= #{startDate} AND b.created_date <= #{endDate}
AND (b.title LIKE CONCAT('%', #{search}, '%') OR b.writer LIKE CONCAT('%', #{search}, '%') OR b.content LIKE CONCAT('%', #{search}, '%'))
AND b.category_id = (SELECT category_id FROM category WHERE name = #{category})
